sys_script_client.script:
var counter=1;
function onLoad() {
   //Type appropriate comment here, and begin script below
   if(!g_form.isNewRecord()){
	   var answersArray = g_form.getValue("answers").split(",");
	   while(counter<=answersArray.length){
	   var sectionid = g_form.getSections()[0].id.split("-")[1];
	$j('#'+sectionid).append("<div class=\"form-group \" style=\"\"> <div class=\"\" data-type=\"label\" choice=\"0\" type=\"string\" nowrap=\"true\"> <label dir=\"ltr\" class=\" col-xs-12 col-md-1_5 col-lg-2 control-label\"><span title=\"\" mandatory=\"false\" class=\" required-marker\" ></span><span title=\"\" class=\"label-text\" data-html=\"false\" >Answer "+counter+"</span></label> </div> <div class=\"col-xs-10 col-md-9 col-lg-8 form-field input_controls\"> <input type=\"text\" id=\"answer"+counter+"\" aria-required=\"false\" maxlength=\"40\" style=\"; \" autocomplete=\"off\" ng-non-bindable=\"\" class=\"form-control \" spellcheck=\"false\" value=\""+answersArray[counter-1]+"\"> </div> <div class=\"col-xs-2 col-md-1_5 col-lg-2 form-field-addons\"></div> </div>");
	counter++;
   }
   }
}
