sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   var gr = new GlideRecord("x_167218_qmt_monitoring_questions");
	gr.addQuery("portal_name","vgeco");
	gr.query();
	var sectionNames = g_form.getSectionNames();
	var sections = g_form.getSections();
	sectionNames = sectionNames.indexOf("questions");
	sectionNames = sections[sectionNames+1].id;
	sectionNames = document.getElementById(sectionNames);
	while(gr.next()){
		var html ="";
		html +="<p>"+gr.question_text+"</p>";
		html+="<p><input type=\"radio\">Pass<input type=\"radio\">Defect</p>";
		var answers = gr.answers;
		answers = answers.split(",");
		sectionNames.innerHTML +="<p>"+gr.question_text+"</p>";
	}
}
