sys_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below
	var ga = new GlideAjax('getAllAnswers');
	ga.addParam("sysparm_name","getPortalQuestions");
	ga.addParam("sysparm_portalname","vgeco");
	ga.getXML(renderTheQuestions);

	function renderTheQuestions(response){
		var questionsWithAnswers = response.responseXML.documentElement.getAttribute("answer");
		questionsWithAnswers = JSON.parse(questionsWithAnswers);
		var sectionNames = g_form.getSectionNames();
	var sections = g_form.getSections();
	sectionNames = sectionNames.indexOf("questions");
	sectionNames = sections[sectionNames+1].id;
	sectionNames = document.getElementById(sectionNames);
	var html ="";
	for(var i =0;i<questionsWithAnswers.length;i++){
		html +="<p>"+questionsWithAnswers[i].question_text+"</p>";
		html+="<p><input type=\"radio\" name=\"passfail"+i+"\" id=\"passfail"+i+"\" value=\"pass\" onclick=\"g_form.setValue(\'score\',parseInt(g_form.getValue(\'score\'))+10);\"  style=\"margin-left:1em;\">Pass<input type=\"radio\" name=\"passfail"+i+"\" id=\"passfail"+i+"\" value=\"fail\" style=\"margin-left:1em;\">Defect</p>";
		var answers = questionsWithAnswers[i].answers;
		answers = answers.split(",");
		html +="<p><select class=\"form-control\">";
		for (var j = 0 ; j<answers.length;j++){
			html +="<option>"+answers[j]+"</option>";
		}
		html+="</select></p>";
	}
	sectionNames.innerHTML +=html;
	}
}

	function deductFromScore(weight){
	g_form.setValue("score",g_form.getValue("score")-weight);
}
function addToScore(weight){
	g_form.setValue("score",g_form.getValue("score")+weight);
}

$j('input[type=radio][id^=passfail]').change(function() {
    if (this.value == 'pass') {
        addToScore(10);
    }
    else if (this.value == 'fail') {
        deductFromScore(10);
    }
});
