sys_script_include.script:
var getAllAnswers = Class.create();
getAllAnswers.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	getPortalQuestions:function(){
	var portalName = this.getParameter("sysparm_portalname");
	var gr = new GlideRecord("x_167218_qmt_monitoring_questions");
	gr.addQuery("portal_name",portalName);
	gr.query();

	var result = [];
	while(gr.next()){	result.push({"question_text":gr.question_text.toString(),"answers":gr.answers.toString()});
	}
		//return JSON.stringify(result);
		return JSON.stringify(result);

},
    type: 'getAllAnswers'
});
