sys_transform_script.script:
// This function creates the Customer if its not on Customer Data Table
// Inputs are Customer Name,Country.
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {


	var customerDataTable = new GlideRecord("x_167218_qmt_customer_data");
	customerDataTable.addQuery("customer_name",source.u_customer);
	customerDataTable.query();
	if(customerDataTable.getRowCount() <1){
		var gr = new GlideRecord('x_167218_qmt_customer_data');
		gr.initialize(); 
		gr.customer_name = source.u_customer;
		gr.country = source.u_country;
		gr.insert();
	}
})(source, map, log, target);
