sys_ui_action.script:
var counter=1;


function createAnswer(){
	var sectionid = g_form.getSections()[0].id.split("-")[1];
	$j('#'+sectionid).append("<div class=\"form-group \" style=\"\"> <div class=\"\" data-type=\"label\" choice=\"0\" type=\"string\" nowrap=\"true\"> <label dir=\"ltr\" class=\" col-xs-12 col-md-1_5 col-lg-2 control-label\"><span title=\"\" mandatory=\"false\" class=\" required-marker\" ></span><span title=\"\" class=\"label-text\" data-html=\"false\" >Answer "+counter+"</span></label> </div> <div class=\"col-xs-10 col-md-9 col-lg-8 form-field input_controls\"> <input type=\"text\" id=\"answer"+counter+"\" aria-required=\"false\" maxlength=\"2000\" style=\"; \" autocomplete=\"off\" ng-non-bindable=\"\" class=\"form-control \" spellcheck=\"false\"> </div> <div class=\"col-xs-2 col-md-1_5 col-lg-2 form-field-addons\"><button type='button' id='delete'>X</button></div> </div>");
	counter++;

	 $j('[id^=delete]').on("click", function() {
	 $j(this).parent().parent().remove();
	 });
}
